/**
 * Alert about starting of process.
 *
 * @param message
 */
export declare const start: (message: string) => void;
/**
 * Alert about successful ending of process.
 *
 * @param message
 */
export declare const done: (message: string) => void;
/**
 * Alert about error that was occurred during process execution.
 *
 * @param message
 */
export declare const oops: (message: string | Error) => void;
/** Shows warning message. */
export declare const warn: (message: string) => void;
